(function(){tinymce.PluginManager.requireLangPack('files');tinymce.create('tinymce.plugins.ExamplePlugin',{init:function(ed,url){ed.addCommand('mceExample',function(){ed.windowManager.open({file:url+'/browse.php',width:620+parseInt(ed.getLang('files.delta_width',0)),height:520+parseInt(ed.getLang('files.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('files',{title:'files.desc',cmd:'mceExample',image:url+'/img/example.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('files',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'Example plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/files',version:"1.0"};}});tinymce.PluginManager.add('files',tinymce.plugins.ExamplePlugin);})();